import React, { Fragment } from 'react';
import { Link, Outlet } from 'react-router-dom';

const StudentDashboard = () => {
  return (
    <Fragment>
      <div className="flex">
        {/* Sidebar */}
        <div className="w-64 h-screen bg-indigo-600 text-white p-4">
          <h2 className="text-2xl font-semibold mb-6">Dashboard</h2>
          <ul>
            <li className="mb-4">
              <Link to="/dashboard/tasks" className="text-white hover:text-indigo-200">View Tasks</Link>
            </li>
            <li className="mb-4">
              <Link to="/dashboard/submitted" className="text-white hover:text-indigo-200">Submitted Tasks</Link>
            </li>
          </ul>
        </div>

        {/* Main content */}
        <div className="flex-1 p-6">
          {/* Render the nested routes here */}
          <Outlet />
        </div>
      </div>
    </Fragment>
  );
};

export default StudentDashboard;
