import React, { Fragment, useState } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
import { Menu } from 'lucide-react'; // Icon for mobile menu
import TasksFromLectures from './tasks';
import SubmitTask from './submit';
import SubmittedTasks from './submittedTasks';

const StudentDashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const location = useLocation();

  const menuItems = [
    { path: "/tasks", label: "View Tasks" },
    { path: "/submit-task", label: "Submit Task" },
    { path: "/submitted", label: "Submitted Tasks" },
  ];

  return (
    <Fragment>
      <div className="flex h-screen bg-gray-100">

        {/* Sidebar */}
        <div className={`bg-indigo-600 text-white p-4 transition-all ${isSidebarOpen ? "w-64" : "w-16"} duration-300`}>
          <div className="flex items-center justify-between mb-6">
            <h2 className={`text-2xl font-semibold ${!isSidebarOpen && "hidden"}`}>Dashboard</h2>
            <button
              className="text-white p-2 hover:bg-indigo-500 rounded-md"
              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            >
              <Menu size={24} />
            </button>
          </div>

          <ul>
            {menuItems.map((item) => (
              <li key={item.path} className="mb-4">
                <Link
                  to={item.path}
                  className={`block px-4 py-2 rounded-md ${
                    location.pathname === item.path ? "bg-indigo-500" : "hover:bg-indigo-500"
                  }`}
                >
                  {item.label}
                </Link>
              </li>
            ))}
          </ul>
        </div>

        {/* Main Content */}
        <div className="flex-1 p-6 overflow-y-auto">
          <Routes>
            <Route path="/tasks" element={<TasksFromLectures />} />
            <Route path="/submit-task" element={<SubmitTask />} />
            <Route path="/submitted" element={<SubmittedTasks />} />
          </Routes>
        </div>
      </div>
    </Fragment>
  );
};

export default StudentDashboard;
