import React, { useState, useEffect, Fragment } from 'react';
import { Link } from 'react-router-dom';

const TasksFromLectures = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // Track errors

  // Fetch tasks from the backend
  const getTasks = async () => {
    try {
      const response = await fetch('http://localhost:5000/tasks');
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data = await response.json();
      setTasks(data); // Update the tasks state with the fetched data
      setLoading(false); // Set loading to false once data is fetched
    } catch (err) {
      setError('There was an issue fetching the tasks. Please try again later.');
      setLoading(false); // Set loading to false in case of error
    }
  };

  // Fetch tasks on component mount
  useEffect(() => {
    getTasks();
  }, []);

  return (
    <Fragment>
      <div className="p-6">
        <h1 className="text-2xl font-semibold mb-4">Tasks from Lecturers</h1>

        {loading ? (
          <p>Loading tasks...</p> // Loading state
        ) : error ? (
          <p className="text-red-500">{error}</p> // Display error message
        ) : tasks.length === 0 ? (
          <p>No tasks available</p> // No tasks available state
        ) : (
          <div className="space-y-4">
            {tasks.map((task) => (
              <div
                key={task.id}
                className="border border-gray-300 p-4 rounded-lg shadow hover:shadow-lg transition"
              >
                <h2 className="text-xl font-semibold">{task.name}</h2>
                <p className="text-gray-600">{task.description}</p>

                <Link
                  to={`/submit/${task.id}`}
                  className="mt-3 text-blue-500 underline"
                >
                  Submit Task
                </Link>
              </div>
            ))}
          </div>
        )}
      </div>
    </Fragment>
  );
};

export default TasksFromLectures;
