import React, { useState, useEffect } from 'react';

const Submissions = () => {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch all submissions
  const fetchSubmissions = async () => {
    try {
      const response = await fetch('http://localhost:5000/submissions', {
        method: 'GET',
      });
      const data = await response.json();
      setSubmissions(data);
      setLoading(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  // Delete a submission
  const deleteSubmission = async (submissionId) => {
    try {
      const confirmDelete = window.confirm('Are you sure you want to delete this submission?');
      if (!confirmDelete) return;

      const response = await fetch(`http://localhost:5000/submissions/${submissionId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        alert('Submission deleted successfully!');
        setSubmissions(submissions.filter((submission) => submission.id !== submissionId));
      } else {
        alert('Failed to delete submission.');
      }
    } catch (err) {
      console.error(err.message);
    }
  };

  // Automatically fetch submissions every 5 seconds
  useEffect(() => {
    fetchSubmissions();
    const interval = setInterval(fetchSubmissions, 5000); // Fetch every 5 seconds
    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-semibold mb-4">Task Submissions</h1>
      {loading ? (
        <p>Loading submissions...</p>
      ) : submissions.length === 0 ? (
        <p>No submissions available.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {submissions.map((submission) => (
            <div
              key={submission.id}
              className="border border-gray-300 p-4 rounded-lg shadow hover:shadow-lg transition"
            >
              <p className="font-medium text-lg">Task ID: {submission.task_id}</p>
              <p className="text-gray-700">Submitted By: {submission.submitted_by}</p>
              <a
                href={`http://localhost:5000/${submission.file_path}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 underline"
              >
                View File
              </a>
              <button
                onClick={() => deleteSubmission(submission.id)}
                className="mt-3 bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600"
              >
                Delete Submission
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Submissions;
