import React, { Fragment } from 'react';
import { Link, Routes, Route } from 'react-router-dom';
import TasksFromLectures from './tasks';
import SubmitTask from './submit';
import SubmittedTasks from './submittedTasks';

const StudentDashboard = () => {
  return (
    <Fragment>
      <div className="flex">
        {/* Sidebar */}
        <div className="w-64 h-screen bg-indigo-600 text-white p-4">
          <h2 className="text-2xl font-semibold mb-6">Dashboard</h2>
          <ul>
            <li className="mb-4">
              <Link to="/student-dashboard/tasks" className="text-white hover:text-indigo-200">
                View Tasks
              </Link>
            </li>
            <li className="mb-4">
              <Link to="/student-dashboard/submit-task" className="text-white hover:text-indigo-200">
                Submit Task
              </Link>
            </li>
            <li className="mb-4">
              <Link to="/student-dashboard/submitted" className="text-white hover:text-indigo-200">
                Submitted Tasks
              </Link>
            </li>
          </ul>
        </div>

        {/* Main content */}
        <div className="flex-1 p-6">
          <Routes>
            <Route path="tasks" element={<TasksFromLectures />} />
            <Route path="submit-task" element={<SubmitTask />} />
            <Route path="submitted" element={<SubmittedTasks />} />
          </Routes>
        </div>
      </div>
    </Fragment>
  );
};

export default StudentDashboard;
