import React, { useState, useEffect, useCallback } from 'react';

const Submissions = ({ taskId }) => {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch submissions for a specific task
  const fetchSubmissions = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/submissions/${taskId}`, {
        method: 'GET',
      });

      if (!response.ok) {
        throw new Error('No submissions found for this task');
      }

      const data = await response.json();
      setSubmissions(data);
      setLoading(false);
    } catch (err) {
      console.error(err.message);
      setLoading(false);
    }
  }, [taskId]);  // Dependency on taskId, so it refetches when taskId changes

  // Delete a specific submission
  const deleteSubmission = async (submissionId) => {
    try {
      const confirmDelete = window.confirm('Are you sure you want to delete this submission?');
      if (!confirmDelete) return;

      const response = await fetch(`http://localhost:5000/submissions/${submissionId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        alert('Submission deleted successfully!');
        setSubmissions(submissions.filter((submission) => submission.id !== submissionId));
      } else {
        alert('Failed to delete submission.');
      }
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    if (taskId) {
      fetchSubmissions();
    }
  }, [taskId, fetchSubmissions]);  // Include fetchSubmissions as a dependency

  return (
    <div className="p-6">
      <h1 className="text-2xl font-semibold mb-4">Submissions for Task ID: {taskId}</h1>
      {loading ? (
        <p>Loading submissions...</p>
      ) : submissions.length === 0 ? (
        <p>No submissions found for this task.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {submissions.map((submission) => (
            <div
              key={submission.id}
              className="border border-gray-300 p-4 rounded-lg shadow hover:shadow-lg transition"
            >
              <p className="font-medium text-lg">Submitted By: {submission.submitted_by}</p>
              <a
                href={`http://localhost:5000/${submission.file_path}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 underline"
              >
                View File
              </a>
              <button
                onClick={() => deleteSubmission(submission.id)}
                className="mt-3 bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600"
              >
                Delete Submission
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Submissions;
