import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import TasksFromLectures from './TasksFromLectures'; // Path to your TasksFromLectures component
import SubmitTask from './SubmitTask'; // Path to your SubmitTask component

const App = () => {
  return (
    <Router>
      <Switch>
        {/* Route for the task list */}
        <Route exact path="/tasks" component={TasksFromLectures} />

        {/* Route for submitting a task (dynamic taskId) */}
        <Route path="/submit/:taskId" component={SubmitTask} />

        {/* Default route */}
        <Route path="/" component={TasksFromLectures} />
      </Switch>
    </Router>
  );
};

export default App;
