import React, { useState, useEffect, Fragment } from 'react';
import { Link, Route, BrowserRouter as Router, Switch } from 'react-router-dom';
import TasksFromLectures from './TasksFromLectures';
import SubmittedTasks from './SubmittedTasks'; // Assuming this component is already created
import { useHistory } from 'react-router-dom';

const StudentDashboard = () => {
  const [userName, setUserName] = useState('Student'); // Assuming username is set here
  const history = useHistory();

  // Logout function
  const logout = () => {
    // Clear session or perform any necessary cleanup
    // Redirect to login page or homepage
    history.push('/login');
  };

  return (
    <Router>
      <Fragment>
        <div className="flex">
          {/* Sidebar */}
          <div className="w-64 h-screen bg-indigo-600 text-white p-4">
            <h2 className="text-2xl font-semibold mb-6">{userName}'s Dashboard</h2>
            <ul>
              <li className="mb-4">
                <Link to="/tasks" className="text-white hover:text-indigo-200">View Tasks</Link>
              </li>
              <li className="mb-4">
                <Link to="/submitted" className="text-white hover:text-indigo-200">Submitted Tasks</Link>
              </li>
              <li>
                <button
                  onClick={logout}
                  className="text-white hover:text-indigo-200 w-full text-left"
                >
                  Logout
                </button>
              </li>
            </ul>
          </div>

          {/* Main content */}
          <div className="flex-1 p-6">
            <Switch>
              <Route path="/tasks" component={TasksFromLectures} />
              <Route path="/submitted" component={SubmittedTasks} />
            </Switch>
          </div>
        </div>
      </Fragment>
    </Router>
  );
};

export default StudentDashboard;
