import React, { useEffect, useState } from 'react';

const Submissions = () => {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [taskId, setTaskId] = useState(""); // Task ID to fetch submissions

  // Fetch submissions for a specific task
  const fetchSubmissions = async () => {
    if (!taskId) return alert("Please enter a Task ID");

    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/submissions/${taskId}`, {
        method: 'GET',
      });

      if (!response.ok) {
        throw new Error("Failed to fetch submissions");
      }

      const data = await response.json();
      setSubmissions(data);
    } catch (err) {
      console.error(err.message);
      alert("Error fetching submissions");
    } finally {
      setLoading(false);
    }
  };

  // Delete a specific submission
  const deleteSubmission = async (id) => {
    if (!window.confirm("Are you sure you want to delete this submission?")) return;

    try {
      const response = await fetch(`http://localhost:5000/submissions/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error("Failed to delete submission");
      }

      alert("Submission deleted successfully");
      setSubmissions((prev) => prev.filter((submission) => submission.id !== id));
    } catch (err) {
      console.error(err.message);
      alert("Error deleting submission");
    }
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold text-center mb-6">Task Submissions</h1>

      {/* Task ID Input */}
      <div className="mb-4 flex justify-center">
        <input
          type="text"
          placeholder="Enter Task ID"
          value={taskId}
          onChange={(e) => setTaskId(e.target.value)}
          className="border rounded-l-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={fetchSubmissions}
          className="bg-blue-500 text-white px-4 py-2 rounded-r-md hover:bg-blue-600 focus:ring-2 focus:ring-blue-500"
        >
          Fetch Submissions
        </button>
      </div>

      {loading ? (
        <p className="text-center text-gray-600">Loading submissions...</p>
      ) : (
        <div>
          {submissions.length === 0 ? (
            <p className="text-center text-gray-600">No submissions found for this task.</p>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {submissions.map((submission) => (
                <div
                  key={submission.id}
                  className="bg-white shadow-md rounded-lg p-4 border border-gray-200"
                >
                  <h2 className="text-lg font-semibold mb-2">Submitted By: {submission.submitted_by}</h2>
                  <p className="text-sm text-gray-600 mb-2">
                    File: <a
                      href={`http://localhost:5000/${submission.file_path}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-500 hover:underline"
                    >
                      View File
                    </a>
                  </p>
                  <button
                    onClick={() => deleteSubmission(submission.id)}
                    className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 focus:ring-2 focus:ring-red-500"
                  >
                    Delete Submission
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Submissions;
