import React, { useState, useEffect, useCallback } from 'react';

const Submissions = ({ taskId }) => {
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch submissions for a specific task
  const fetchSubmissions = useCallback(async () => {
    try {
      const response = await fetch(`http://localhost:5000/submissions/${taskId}`, {
        method: 'GET',
      });

      if (!response.ok) {
        throw new Error('No submissions found for this task');
      }

      const data = await response.json();
      setSubmissions(data);
      setLoading(false);
    } catch (err) {
      console.error(err.message);
      setLoading(false);
    }
  }, [taskId]);

  // Approve or reject a submission
  const updateSubmissionStatus = async (submissionId, status) => {
    try {
      const response = await fetch(`http://localhost:5000/submissions/${submissionId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      });

      if (!response.ok) {
        const data = await response.json();
        alert(data.error || 'Failed to update submission status');
        return;
      }

      // Update local submission list
      setSubmissions(submissions.map(submission =>
        submission.id === submissionId ? { ...submission, status } : submission
      ));
      alert('Submission status updated successfully!');
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    if (taskId) {
      fetchSubmissions();
    }
  }, [taskId, fetchSubmissions]);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-semibold mb-4">Submissions for Task ID: {taskId}</h1>
      {loading ? (
        <p>Loading submissions...</p>
      ) : submissions.length === 0 ? (
        <p>No submissions found for this task.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {submissions.map((submission) => (
            <div
              key={submission.id}
              className="border border-gray-300 p-4 rounded-lg shadow hover:shadow-lg transition"
            >
              <p className="font-medium text-lg">Submitted By: {submission.submitted_by}</p>
              <p>Status: {submission.status}</p>
              <a
                href={`http://localhost:5000/${submission.file_path}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-500 underline"
              >
                View File
              </a>
              <div className="mt-3">
                <button
                  onClick={() => updateSubmissionStatus(submission.id, 'approved')}
                  className="bg-green-500 text-white py-1 px-3 rounded hover:bg-green-600"
                >
                  Approve
                </button>
                <button
                  onClick={() => updateSubmissionStatus(submission.id, 'rejected')}
                  className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600 ml-2"
                >
                  Reject
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Submissions;
