import React from 'react';
import { BrowserRouter as Router, Route, Routes,Navigate } from 'react-router-dom';
import StudentDashboard from './students/studentDashboard';
import TasksFromLectures from './students/tasks'; // Path to your TasksFromLectures component
import SubmitTask from './students/submit'; // Path to your SubmitTask component


const App = () => {
  return (
    <Router>
      <Routes>
        {/* Route for the task list */}
        <Route path="/tasks" element={<TasksFromLectures />} />

        {/* Route for submitting a task (dynamic taskId) */}
        <Route path="/submit/:taskId" element={<SubmitTask />} />

        {/* Redirect from the home route to /tasks */}
        <Route path="/" element={<Navigate to="/tasks" />} />

        {/* Default route for unmatched paths */}
        <Route path="*" element={<h2>Page Not Found</h2>} />
      </Routes>
    </Router>
  );
};

export default App;
