import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import LectureDashboard from './Lecturers/lecturersDashboard';
import StudentDashboard from './students/studentDashboard';
import RegisterForm from './components/userRegister';
import LoginForm from './components/usersLogin';

function App() {
  const [userRole, setUserRole] = useState(null); // to store user role after login
  const navigate = useNavigate();

  useEffect(() => {
    // If the user is already logged in, check their role (from localStorage or backend)
    const storedRole = localStorage.getItem('userRole'); // or get from backend
    if (storedRole) {
      setUserRole(storedRole);
      if (storedRole === 'lecturer') {
        navigate('/lecturer-dashboard');
      } else if (storedRole === 'student') {
        navigate('/student-dashboard');
      }
    }
  }, []);

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<LoginForm setUserRole={setUserRole} />} />
          <Route path="/register" element={<RegisterForm />} />
          <Route path="/student-dashboard" element={<StudentDashboard />} />
          <Route path="/lecturer-dashboard" element={<LectureDashboard />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
