import React, { useState, createContext, useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './components/usersLogin';
import RegisterForm from './components/userRegister';
import AdminLogin from './Admin/adminLogin';
import TaskManagement from './Admin/taskManagement';
import Lecturers from './Admin/lectures';
import Users from './Admin/usersManagement';
import Tasks from './Lecturers/task';
import Submissions from './Lecturers/submission';
import StudentsTasks from './students/tasks';
import SubmissionTasks from './students/submit';
import Navigation from './components/navigation';
import AdminSidebarNavigation from './Admin/adminSidebar';

// Authentication Context
const AuthContext = createContext();

function App() {
  const [authState, setAuthState] = useState({
    role: null, // 'admin', 'student', 'lecturer'
    isAuthenticated: false,
  });

  const login = (role) => setAuthState({ role, isAuthenticated: true });
  const logout = () => setAuthState({ role: null, isAuthenticated: false });

  return (
    <AuthContext.Provider value={{ authState, login, logout }}>
      <Router>
        <div className="App">
          <Routes>
            {/* Admin Routes */}
            <Route
              path="/admin/*"
              element={
                authState.role === 'admin' && authState.isAuthenticated ? (
                  <AdminDashboard />
                ) : (
                  <Navigate to="/admin/login" />
                )
              }
            />
            <Route
              path="/admin/login"
              element={
                authState.role === 'admin' && authState.isAuthenticated ? (
                  <Navigate to="/admin/tasks" />
                ) : (
                  <AdminLogin onLogin={() => login('admin')} />
                )
              }
            />

            {/* Shared Login and Registration Routes */}
            <Route
              path="/login"
              element={<LoginForm onLogin={(role) => login(role)} />}
            />
            <Route path="/register" element={<RegisterForm />} />

            {/* Lecturer Routes */}
            <Route
              path="/lecturer/*"
              element={
                authState.role === 'lecturer' && authState.isAuthenticated ? (
                  <LecturerDashboard />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />

            {/* Student Routes */}
            <Route
              path="/student/*"
              element={
                authState.role === 'student' && authState.isAuthenticated ? (
                  <StudentDashboard />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
          </Routes>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;

// Admin Dashboard Component
function AdminDashboard() {
  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <AdminSidebarNavigation />
      {/* Main Content */}
      <div className="w-3/4 p-6 overflow-y-auto">
        <Routes>
          <Route path="tasks" element={<TaskManagement />} />
          <Route path="lecturers" element={<Lecturers />} />
          <Route path="users" element={<Users />} />
        </Routes>
      </div>
    </div>
  );
}

// Lecturer Dashboard Component
function LecturerDashboard() {
  return (
    <>
      <Navigation />
      <div className="p-6">
        <Routes>
          <Route path="tasks" element={<Tasks />} />
          <Route path="submissions" element={<Submissions />} />
        </Routes>
      </div>
    </>
  );
}

// Student Dashboard Component
function StudentDashboard() {
  return (
    <>
      <Navigation />
      <div className="p-6">
        <Routes>
          <Route path="tasks" element={<StudentsTasks />} />
          <Route path="submit" element={<SubmissionTasks />} />
        </Routes>
      </div>
    </>
  );
}
