import React, { useState, useEffect } from 'react';
import Submissions from './submission';
import Tasks from './task';

const LectureDashboard = ({ lecturerId }) => {
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch(`http://localhost:5000/tasks`);
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data = await response.json();
      setTasks(data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-semibold mb-6">Lecturer Dashboard</h1>

      {/* Display Task Management Component */}
      <Tasks />

      {/* Loading and Error Handling */}
      {loading && <p>Loading tasks...</p>}
      {error && <p className="text-red-500">{error}</p>}

      {/* Task List */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-6">
        {tasks.map((task) => (
          <div key={task.id} className="bg-white p-4 rounded-lg shadow">
            <h2 className="text-lg font-semibold">{task.title}</h2>
            <p className="text-gray-600">{task.description}</p>
            <button
              onClick={() => setSelectedTaskId(task.id)}
              className="mt-3 bg-blue-500 text-white py-1 px-3 rounded hover:bg-blue-600"
            >
              View Submissions
            </button>
          </div>
        ))}
      </div>

      {/* Display Submissions Only When a Task is Selected */}
      {selectedTaskId && (
        <div className="mt-8">
          <Submissions taskId={selectedTaskId} />
        </div>
      )}
    </div>
  );
};

export default LectureDashboard;
