import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import TasksFromLectures from './students/tasks'; // Path to your TasksFromLectures component
import SubmitTask from './students/submit'; // Path to your SubmitTask component

const App = () => {
  return (
    <Router>
      <Switch>
        {/* Route for the task list */}
        <Route exact path="/tasks" component={TasksFromLectures} />

        {/* Route for submitting a task (dynamic taskId) */}
        <Route path="/submit/:taskId" component={SubmitTask} />

        {/* Redirect from the home route to /tasks */}
        <Route exact path="/" render={() => <Redirect to="/tasks" />} />

        {/* Default route for unmatched paths */}
        <Route render={() => <h2>Page Not Found</h2>} />
      </Switch>
    </Router>
  );
};

export default App;
