import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import StudentDashboard from './students/studentDashboard'; // Dashboard component
import TasksFromLectures from './students/tasks'; // Tasks component
import SubmittedTasks from './students/submittedTasks'; // Submitted tasks component

const App = () => {
  return (
    <Router>
      <Routes>
        {/* Route for the student dashboard */}
        <Route path="/dashboard" element={<StudentDashboard />}>
          {/* Nested routes for tasks and submitted tasks inside the dashboard */}
          <Route path="tasks" element={<TasksFromLectures />} />
          <Route path="submitted" element={<SubmittedTasks />} />
        </Route>

        {/* Redirect from the home route to /dashboard */}
        <Route path="/" element={<Navigate to="/dashboard" />} />

        {/* Default route for unmatched paths */}
        <Route path="*" element={<h2>Page Not Found</h2>} />
      </Routes>
    </Router>
  );
};

export default App;
