import React, { useState, useEffect, Fragment } from 'react';
import { Link } from 'react-router-dom';

const TasksFromLectures = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch tasks from the backend
  const getTasks = async () => {
    try {
      const response = await fetch('http://localhost:5000/tasks');
      if (!response.ok) {
        throw new Error('Failed to fetch tasks');
      }
      const data = await response.json();
      setTasks(data);
    } catch (err) {
      setError(err.message || 'There was an issue fetching the tasks. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Fetch tasks on component mount
  useEffect(() => {
    getTasks();
  }, []);

  // Handle task deletion
  const handleDeleteTask = async (taskId) => {
    try {
      const response = await fetch(`http://localhost:5000/tasks/${taskId}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        throw new Error('Failed to delete task');
      }
      // Remove the task from the state
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (err) {
      setError(err.message || 'There was an issue deleting the task. Please try again later.');
    }
  };

  return (
    <Fragment>
      <div className="p-6 max-w-3xl mx-auto">
        <h1 className="text-3xl font-bold mb-6 text-gray-800 text-center">
          Tasks from Lecturers
        </h1>

        {/* Refresh Button */}
        <div className="flex justify-end mb-4">
          <button
            onClick={getTasks}
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-300"
          >
            Refresh Tasks
          </button>
        </div>

        {loading ? (
          <div className="flex justify-center items-center">
            <div className="animate-spin rounded-full h-12 w-12 border-t-4 border-blue-500"></div>
          </div>
        ) : error ? (
          <p className="text-red-600 text-center">{error}</p>
        ) : tasks.length === 0 ? (
          <p className="text-center text-gray-600">No tasks available</p>
        ) : (
          <div className="space-y-6">
            {tasks.map((task) => (
              <div
                key={task.id}
                className="border border-gray-300 p-5 rounded-lg shadow-lg hover:shadow-xl transition duration-300 bg-white"
              >
                <h2 className="text-xl font-semibold text-gray-900">{task.name}</h2>
                <p className="text-gray-700">{task.description}</p>

                <div className="mt-3 flex justify-between items-center">
                  <Link
                    to={`/submissions/${task.id}`}
                    className="text-blue-600 hover:underline font-medium"
                  >
                    View Submissions â†’
                  </Link>
                  <button
                    onClick={() => handleDeleteTask(task.id)}
                    className="text-red-600 hover:underline font-medium"
                  >
                    Delete Task
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </Fragment>
  );
};

export default TasksFromLectures;
